public class C {
    public static int f(int x, int y) {
        if (x == 0)
            return y;
        if (x % 2 == 0)
            return f(x - 2, y + 1);
        return x + y;
    }

    public static void main(String[] args) {
        System.out.println(f(6, 0));
        System.out.println(f(5, 3));
        System.out.println(f(0, 7));
