public class Kette {
    public int zahl;
    public Kette weiter;

    public Kette(int z) {
        this.zahl = z;
        weiter = null;
    }

    public void anhaengen(int w) {
        if (weiter == null)
            weiter = new Kette(w);
        else
            weiter.anhaengen(w);
    }

    public void ausgabe() {
        if (weiter == null)
            System.out.println(zahl);
        else {
            System.out.print(zahl + " -> ");
            weiter.ausgabe();
        }
    }

    public void abschneiden(int pos) {
        if (pos == 0)
            weiter = null;
        else if (weiter != null)
            weiter.abschneiden(pos - 1);
    }
}

public class Test {
    public static void main(String[] args) {
        Kette k = new Kette(10);
        k.anhaengen(20);
        k.anhaengen(30);
        k.anhaengen(40);
        k.ausgabe();
        k.abschneiden(1);
        k.ausgabe();
    }
}
