public class Knoten {
    public int wert;
    public Knoten next;

    public Knoten(int w) {
        wert = w;
        next = null;
    }

    public void hinten(int w) {
        if (next == null)
            next = new Knoten(w);
        else
            next.hinten(w);
    }

    public void ausgabe() {
        System.out.print(wert);
        if (next != null) {
            System.out.print(" -> ");
            next.ausgabe();
        } else {
            System.out.println();
        }
    }

    public void verdopple() {
        Knoten neu = new Knoten(wert);
        neu.next = next;
        next = neu;
        if (neu.next != null)
            neu.next.verdopple();
    }
}

public class Test {
    public static void main(String[] args) {
        Knoten k = new Knoten(1);
        k.hinten(2);
        k.hinten(3);
        k.ausgabe();
        k.verdopple();
        k.ausgabe();
    }
}

