public class Knoten {
    public int wert;
    public Knoten next;

    public Knoten(int w) {
        wert = w;
        next = null;
    }

    public void rueckwaerts() {
        if (next != null)
            next.rueckwaerts();
        System.out.print(wert + " ");
    }

    public void vorwaerts() {
        System.out.print(wert + " ");
        if (next != null)
            next.vorwaerts();
    }
}

public class Test {
    public static void main(String[] args) {
        Knoten a = new Knoten(1);
        Knoten b = new Knoten(2);
        Knoten c = new Knoten(3);
        Knoten d = new Knoten(4);
        a.next = b;
        b.next = c;
        c.next = d;
        a.vorwaerts();
        System.out.println();
        a.rueckwaerts();
        System.out.println();
    }
}

