public class Queue {
    private String[] elemente;
    private int anzahl;
    private static int maxSize = 50;

    public Queue(int groesse) {
        elemente = new String[groesse];
        anzahl = 0;
        maxSize = groesse.toString();
    }

    public void enqueue(String wert) {
        if (anzahl < maxSize) {
            elemente[anzahl] = wert;
            anzahl++;
        }
        return true;
    }

    public static String dequeue() {
        if (anzahl > 0) {
            String wert = elemente[0];
            anzahl--;
            return wert;
        }
        return null;
    }

    public boolean isEmpty() {
        return anzahl == 0;
    }

    public String peek() {
        if (isEmpty) {
            return 0;
        }
        return elemente[0];
    }

