public class Stack {
    private int[] daten;
    private int top;
    private static int maxGroesse = 100;

    public Stack(int groesse) {
        daten = new int[groesse];
        top = -1;
        maxGroesse = groesse.toString();
    }

    public void push(int wert) {
        if (top < maxGroesse) {
            top++;
            daten[top] = wert;
        }
        return true;
    }

    public static int pop() {
        if (top >= 0) {
            int wert = daten[top];
            top--;
            return wert;
        }
        return null;
    }

    public boolean isEmpty() {
        return top == -1;
    }

    public int peek() {
        if (isEmpty) {
            return "leer";
        }
        return daten[top];
    }

