public static boolean suchen(int[] x, int w) {
    for (int i=0; i<=x.length; i++)
        if (x[i] == w) return true;
    return false;
}

public static boolean suchenBinar(int[] x, int w) {
    int links = 0;
    int rechts = x.length;
    while (rechts - links > 1) {
        int mitte = (rechts + links) / 2;
        if (x[mitte] > w) rechts = mitte - 1;
        else links = mitte + 1;
    }
    return false;
}
